var MAX_RELATED = 8;
function getCurrentSlug() {
var path = window.location.pathname;
var match = path.match(/\/tools\/([^/]+)\/?$/);
return match ? match[1] : null;
}
function findCategory(slug) {
var keys = Object.keys(CATEGORIES);
for (var i = 0; i < keys.length; i++) {
if (CATEGORIES[keys[i]].indexOf(slug) !== -1) {
return keys[i];
}
}
return null;
}
function shuffle(arr) {
var a = arr.slice();
for (var i = a.length - 1; i > 0; i--) {
var j = Math.floor(Math.random() * (i + 1));
var tmp = a[i];
a[i] = a[j];
a[j] = tmp;
}
return a;
}
function getRelatedTools(slug) {
var category = findCategory(slug);
if (!category) return [];
var result = [];
var seen = {};
seen[slug] = true;
var sameCategory = shuffle(CATEGORIES[category]);
for (var i = 0; i < sameCategory.length && result.length < MAX_RELATED; i++) {
var t = sameCategory[i];
if (!seen[t]) {
seen[t] = true;
result.push(t);
}
}
var adj = ADJACENT[category] || [];
for (var a = 0; a < adj.length && result.length < MAX_RELATED; a++) {
var adjTools = shuffle(CATEGORIES[adj[a]] || []);
for (var j = 0; j < adjTools.length && result.length < MAX_RELATED; j++) {
var at = adjTools[j];
if (!seen[at]) {
seen[at] = true;
result.push(at);
}
}
}
return result;
}
function buildCardHTML(slug) {
var name = TOOL_DATA[slug] || slug;
var desc = TOOL_DESC[slug] || "";
var href = "../" + slug + "/";
return '<a href="' + href + '" class="related-tools__card">' +
'<div class="related-tools__card-name">' + name + '</div>' +
'<div class="related-tools__card-desc">' + desc + '</div>' +
'</a>';
}
function enhanceRelatedTools() {
var slug = getCurrentSlug();
if (!slug) return;
var section = document.querySelector(".related-tools");
if (!section) return;
var tools = getRelatedTools(slug);
if (tools.length === 0) return;
var existingUl = section.querySelector("ul");
if (!existingUl) return;
var grid = document.createElement("div");
grid.className = "related-tools__grid";
for (var i = 0; i < tools.length; i++) {
grid.innerHTML += buildCardHTML(tools[i]);
}
existingUl.parentNode.replaceChild(grid, existingUl);
}
document.addEventListener("DOMContentLoaded", enhanceRelatedTools);
